import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Tab, Tabs, Container, Row, Col } from 'react-bootstrap';
import './Pokedex.scss';

interface Ability {
    name: string;
    is_hidden: boolean;
}

interface Stat {
    name: string;
    base_stat: number;
}

interface Type {
    name: string;
}

interface PokemonForm {
    id: number;
    name: string;
    abilities: Ability[];
    height: number;
    weight: number;
    stats: Stat[];
    types: Type[];
}

interface PokemonSpecies {
    name: string;
    nationalDexNumber: number;
    generation: string;
    genus: string;
    forms: PokemonForm[];
}

const PokemonDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [pokemon, setPokemon] = useState<PokemonSpecies | null>(null);
    const [selectedForm, setSelectedForm] = useState<number>(0);

    useEffect(() => {
        const fetchPokemonDetails = async () => {
            try {
                const speciesResponse = await axios.get(`https://pokeapi.co/api/v2/pokemon-species/${id}/`);
                const speciesData = speciesResponse.data;

                // Extract relevant data
                const nationalDexNumber = speciesData.pokedex_numbers.find((entry: any) => entry.pokedex.name === 'national').entry_number;
                const generation = speciesData.generation.name.replace('generation-', 'Generation ').toUpperCase();
                const genus = speciesData.genera.find((entry: any) => entry.language.name === 'en').genus;

                // Fetch forms
                const formsData = await Promise.all(
                    speciesData.varieties.map(async (variety: any) => {
                        const formResponse = await axios.get(variety.pokemon.url);
                        const formData = formResponse.data;
                        return {
                            id: formData.id,
                            name: formData.name,
                            abilities: formData.abilities.map((ability: any) => ({
                                name: ability.ability.name,
                                is_hidden: ability.is_hidden,
                            })),
                            height: formData.height / 10, // Convert decimeters to meters
                            weight: formData.weight / 10, // Convert hectograms to kilograms
                            stats: formData.stats.map((stat: any) => ({
                                name: stat.stat.name,
                                base_stat: stat.base_stat,
                            })),
                            types: formData.types.map((type: any) => ({
                                name: type.type.name,
                            })),
                        };
                    })
                );

                // Set default form
                const defaultFormIndex = speciesData.varieties.findIndex((variety: any) => variety.is_default);

                setPokemon({
                    name: speciesData.name,
                    nationalDexNumber,
                    generation,
                    genus,
                    forms: formsData,
                });

                setSelectedForm(defaultFormIndex);
            } catch (error) {
                console.error('Error fetching Pokémon details:', error);
            }
        };

        fetchPokemonDetails();
    }, [id]);

    if (!pokemon) {
        return <div>Loading...</div>;
    }

    const form = pokemon.forms[selectedForm];

    return (
        <Container>
            <h1>{pokemon.name.charAt(0).toUpperCase() + pokemon.name.slice(1)}</h1>
            <p><strong>National Dex Number:</strong> #{pokemon.nationalDexNumber}</p>
            <p><strong>Generation:</strong> {pokemon.generation}</p>
            <p><strong>Genus:</strong> {pokemon.genus}</p>

            <Tabs activeKey={selectedForm} onSelect={(k) => setSelectedForm(Number(k))} className="mb-3">
                {pokemon.forms.map((form, index) => (
                    <Tab eventKey={index} title={form.name} key={form.id}>
                        <Row>
                            <Col md={4}>
                                <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${form.id}.png`} alt={form.name} style={{ width: '100%' }} />
                            </Col>
                            <Col md={8}>
                                <h3>Abilities</h3>
                                <ul>
                                    {form.abilities.map((ability, idx) => (
                                        <li key={idx}>
                                            {ability.name} {ability.is_hidden && '(Hidden)'}
                                        </li>
                                    ))}
                                </ul>
                                <h3>Stats</h3>
                                <ul>
                                    {form.stats.map((stat, idx) => (
                                        <li key={idx}>
                                            {stat.name.toUpperCase()}: {stat.base_stat}
                                        </li>
                                    ))}
                                </ul>
                                <h3>Types</h3>
                                <ul>
                                    {form.types.map((type, idx) => (
                                        <li key={idx}>{type.name.toUpperCase()}</li>
                                    ))}
                                </ul>
                                <p><strong>Height:</strong> {form.height} m</p>
                                <p><strong>Weight:</strong> {form.weight} kg</p>
                            </Col>
                        </Row>
                    </Tab>
                ))}
            </Tabs>
        </Container>
    );
};

export default PokemonDetail;
