import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { Container, Tabs, Tab } from 'react-bootstrap';
import PokemonBasicInfo from './PokemonBasicInfo';
import PokemonLearnset from './PokemonLearnset'; // The second tab, currently blank

interface Ability {
    name: string;
    is_hidden: boolean;
}

interface Stat {
    name: string;
    base_stat: number;
}

interface Type {
    name: string;
}

interface PokemonForm {
    id: number;
    name: string;
    abilities: Ability[];
    height: number;
    weight: number;
    stats: Stat[];
    types: Type[];
}

interface PokemonSpecies {
    name: string;
    nationalDexNumber: number;
    generation: string;
    genus: string;
    varieties: any[];
    evolution_chain: string;
}

const PokemonDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [pokemonForm, setPokemonForm] = useState<PokemonForm | null>(null);
    const [pokemonSpecies, setPokemonSpecies] = useState<PokemonSpecies | null>(null);

    useEffect(() => {
        const fetchPokemonForm = async () => {
            try {
                const formResponse = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}/`);
                const formData = formResponse.data;

                // Fetch species data using the species URL
                const speciesResponse = await axios.get(formData.species.url);
                const speciesData = speciesResponse.data;

                // Extract relevant data for species
                const nationalDexNumber = speciesData.pokedex_numbers.find((entry: any) => entry.pokedex.name === 'national').entry_number;
                const generation = speciesData.generation.name.replace('generation-', 'Generation ').toUpperCase();
                const genus = speciesData.genera.find((entry: any) => entry.language.name === 'en').genus;
                const varieties = speciesData.varieties;
                const evolutionChainUrl = speciesData.evolution_chain.url;

                setPokemonForm({
                    id: formData.id,
                    name: formData.name,
                    abilities: formData.abilities.map((ability: any) => ({
                        name: ability.ability.name,
                        is_hidden: ability.is_hidden,
                    })),
                    height: formData.height / 10, // Convert decimeters to meters
                    weight: formData.weight / 10, // Convert hectograms to kilograms
                    stats: formData.stats.map((stat: any) => ({
                        name: stat.stat.name,
                        base_stat: stat.base_stat,
                    })),
                    types: formData.types.map((type: any) => ({
                        name: type.type.name,
                    })),
                });

                // Fetch the evolution chain data
                const evolutionResponse = await axios.get(evolutionChainUrl);
                const evolutionChain = evolutionResponse.data.chain;

                setPokemonSpecies({
                    name: speciesData.name,
                    nationalDexNumber,
                    generation,
                    genus,
                    varieties,
                    evolution_chain: evolutionChain,
                });
            } catch (error) {
                console.error('Error fetching Pokémon details:', error);
            }
        };

        fetchPokemonForm();
    }, [id]);

    if (!pokemonForm || !pokemonSpecies) {
        return <div>Loading...</div>;
    }

    return (
        <Container>
            <h1>
                {pokemonSpecies.name.charAt(0).toUpperCase() + pokemonSpecies.name.slice(1)} - #{String(pokemonSpecies.nationalDexNumber).padStart(3, '0')}
            </h1>
            <p>
                {pokemonSpecies.generation} - {pokemonSpecies.genus}
            </p>

            <Tabs defaultActiveKey="info" className="mb-3">
                <Tab eventKey="info" title="Info">
                    <PokemonBasicInfo form={pokemonForm} species={pokemonSpecies} />
                </Tab>
                <Tab eventKey="learnset" title="Learnset">
                    <PokemonLearnset />
                </Tab>
            </Tabs>
        </Container>
    );
};

export default PokemonDetail;
