import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, ListGroup, Spinner } from 'react-bootstrap';

interface PokemonSpecies {
    name: string;
    url: string;
}

const Pokedex: React.FC = () => {
    const [pokemonSpecies, setPokemonSpecies] = useState<PokemonSpecies[]>([]);
    const [nextUrl, setNextUrl] = useState<string | null>(null);
    const [loading, setLoading] = useState<boolean>(true);

    useEffect(() => {
        fetchPokemonSpecies('https://pokeapi.co/api/v2/pokemon-species?offset=0&limit=20');
    }, []);

    const fetchPokemonSpecies = async (url: string) => {
        setLoading(true);
        try {
            const response = await axios.get(url);
            setPokemonSpecies(prevSpecies => [...prevSpecies, ...response.data.results]);
            setNextUrl(response.data.next);
        } catch (error) {
            console.error('Error fetching Pokémon species:', error);
        }
        setLoading(false);
    };

    return (
        <div>
            <h1>Pokedex</h1>
            <ListGroup>
                {pokemonSpecies.map((species, index) => (
                    <ListGroup.Item key={index}>
                        {species.name.charAt(0).toUpperCase() + species.name.slice(1)}
                    </ListGroup.Item>
                ))}
            </ListGroup>
            {loading && <Spinner animation="border" />}
            {nextUrl && !loading && (
                <Button onClick={() => fetchPokemonSpecies(nextUrl)} className="mt-3">
                    Load More
                </Button>
            )}
        </div>
    );
};

export default Pokedex;
