import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import axios from 'axios';
import { Container, Tabs, Tab } from 'react-bootstrap';
import PokemonBasicInfo from './PokemonBasicInfo';
import PokemonLearnset from './PokemonLearnset';
import PokemonSprites from './PokemonSprites';
import PokemonEntries from './PokemonEntries';
import { PokemonForm, PokemonSpecies, GenericMoveDetails } from '../../types';
import PokemonLocations from './PokemonLocations';

const fetchPokemonForm = async (id: string): Promise<PokemonForm> => {
    const { data } = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}/`);
    return data;
};

const fetchPokemonSpecies = async (url: string): Promise<PokemonSpecies> => {
    const { data } = await axios.get(url);
    return data;
};

const fetchMoveDetails = async (url: string) => {
    const { data } = await axios.get(url);
    return data;
};

const PokemonDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [pokemonForm, setPokemonForm] = useState<PokemonForm | null>(null);
    const [pokemonSpecies, setPokemonSpecies] = useState<PokemonSpecies | null>(null);
    const [pokemonLearnsets, setPokemonLearnsets] = useState<GenericMoveDetails[]>([]);

    const { data: pokemonForm2, isLoading: isLoadingForm, error: formError } = useQuery({
        queryKey: ['pokemonForm', id],
        queryFn: () => fetchPokemonForm(id!),
        enabled: !!id,
    });

    const speciesUrl = pokemonForm2?.species.url;
    const { data: pokemonSpecies2, isLoading: isLoadingSpecies, error: speciesError } = useQuery({
        queryKey: ['pokemonSpecies', speciesUrl],
        queryFn: () => fetchPokemonSpecies(speciesUrl!),
        enabled: !!speciesUrl,
    });

    const moveUrls = pokemonForm2?.moves.map((move: any) => move.move.url) ?? [];
    const { data: moveDetailsArray = [], isLoading: isLoadingMoves, error: movesError } = useQuery({
        queryKey: ['moveDetails', moveUrls],
        queryFn: () =>
            Promise.all(
                moveUrls.map((url) => fetchMoveDetails(url))
            ),
        enabled: !!pokemonForm?.moves,
    });




    console.log('pokemonForm2:', pokemonForm2);
    console.log('pokemonSpecies2:', pokemonSpecies2);

    useEffect(() => {
        const fetchPokemonForm = async () => {
            try {
                const formResponse = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}/`);
                const formData = formResponse.data;

                // species data
                const speciesResponse = await axios.get(formData.species.url);
                const speciesData = speciesResponse.data;

                const nationalDexNumber = speciesData.pokedex_numbers.find((entry: any) => entry.pokedex.name === 'national').entry_number;
                const generation = speciesData.generation.name.replace('generation-', 'Generation ').toUpperCase();
                const genus = speciesData.genera.find((entry: any) => entry.language.name === 'en').genus;
                const varieties = speciesData.varieties;
                const evolutionChainUrl = speciesData.evolution_chain.url;

                setPokemonForm({
                    id: formData.id,
                    name: formData.name,
                    abilities: formData.abilities.map((ability: any) => ({
                        name: ability.ability.name,
                        is_hidden: ability.is_hidden,
                    })),
                    height: formData.height / 10,
                    weight: formData.weight / 10,
                    stats: formData.stats.map((stat: any) => ({
                        name: stat.stat.name,
                        base_stat: stat.base_stat,
                        effort: stat.effort,
                    })),
                    types: formData.types.map((type: any) => ({
                        name: type.type.name,
                    })),
                    base_experience: formData.base_experience,
                    sprites: formData.sprites,
                    moves: formData.moves,  
                });

                // evolution chain data
                const evolutionResponse = await axios.get(evolutionChainUrl);
                const evolutionChain = evolutionResponse.data.chain;

                // Set state
                setPokemonSpecies({
                    name: speciesData.name,
                    nationalDexNumber,
                    generation,
                    genus,
                    varieties,
                    evolution_chain: evolutionChain,
                    capture_rate: speciesData.capture_rate,
                    base_happiness: speciesData.base_happiness,
                    growth_rate: speciesData.growth_rate,
                    egg_groups: speciesData.egg_groups,
                    gender_rate: speciesData.gender_rate,
                    hatch_counter: speciesData.hatch_counter,
                    flavor_text_entries: speciesData.flavor_text_entries, 
                });

                // learnset data (oh god this was fucked up)

                const moveDataMap: { [moveName: string]: { id: number, power: number, accuracy: number, pp: number, damage_class: string, type: string, past_values: any[], machines: any[] } } = {};
                const uniqueMoves: string[] = [...new Set<string>(formData.moves.map((move: any) => move.move.name))];

                await Promise.all(uniqueMoves.map(async (moveName: string) => {
                    const move = formData.moves.find((m: any) => m.move.name === moveName);
                    const { data: moveData } = await axios.get(move.move.url);

                    moveDataMap[moveName] = {
                        id: moveData.id,
                        power: moveData.power,
                        accuracy: moveData.accuracy,
                        pp: moveData.pp,
                        damage_class: moveData.damage_class.name,
                        type: moveData.type.name,
                        past_values: moveData.past_values,
                        machines: moveData.machines
                    };
                }));

                console.log('moveDataMap:', moveDataMap);

                const flattenedMoves = formData.moves.flatMap((move: any) =>
                    move.version_group_details.map((detail: any) => ({
                        name: move.move.name,
                        url: move.move.url,
                        version_group: detail.version_group.name,
                        version_group_id: parseInt(detail.version_group.url.split('/').slice(-2, -1)[0]),
                        level_learned_at: detail.level_learned_at,
                        learn_method: detail.move_learn_method.name,
                    }))
                );

                const moveDetailsArray: GenericMoveDetails[] = [];

                for (const move of flattenedMoves) {
                    // Fetch details from the moveDataMap
                    const moveData = moveDataMap[move.name];
                    let { id, power, accuracy, pp, type, damage_class } = moveData;

                    // Check past_values to adjust the move details based on the version group
                    moveData.past_values.forEach((pastValue: any) => {
                        const pastVersionId = parseInt(pastValue.version_group.url.split('/').slice(-2, -1)[0]);
                        if (move.version_group_id < pastVersionId) {
                            if (pastValue.power !== null) power = pastValue.power;
                            if (pastValue.accuracy !== null) accuracy = pastValue.accuracy;
                            if (pastValue.pp !== null) pp = pastValue.pp;
                            if (pastValue.type !== null) type = pastValue.type.name;
                        }
                    });

                    // removed machine data because too many api calls

                    /*
                    let machine = '';
                    if (move.learn_method === 'machine') {
                        const machineEntry = moveDataMap[move.name].machines.find(
                            (m: any) => m.version_group.name === move.version_group
                        );
                        if (machineEntry) {
                            const { data: machineData } = await axios.get(machineEntry.machine.url);
                            machine = machineData.item.name;
                        }
                    }*/

                    moveDetailsArray.push({
                        id: id,
                        name: move.name,
                        power,
                        accuracy,
                        pp,
                        damage_class,
                        type,
                        level_learned_at: move.level_learned_at,
                        machine: '',
                        learn_method: move.learn_method,
                        version_group: move.version_group,
                        version_group_id: move.version_group_id,
                    });
                }
                //console.log('moveDetailsArray:', moveDetailsArray);

                const finalMoveDetailsArray = moveDetailsArray;

                setPokemonLearnsets(finalMoveDetailsArray);
                //console.log('pokemonLearnsets after setting:', pokemonLearnsets);
                //console.log('final:', finalMoveDetailsArray);
            } catch (error) {
                console.error('Error fetching PokÃ©mon details:', error);
            }
        };

        fetchPokemonForm();
    }, [id]);



    useEffect(() => {
       //console.log('pokemonLearnsets:', pokemonLearnsets);
    }, [pokemonLearnsets]);

    if (!pokemonForm || !pokemonSpecies) {
        return <div>Loading...</div>;
    }


    return (
        <Container>
            <h1>
                {pokemonSpecies.name.charAt(0).toUpperCase() + pokemonSpecies.name.slice(1)} - #{String(pokemonSpecies.nationalDexNumber).padStart(3, '0')}
            </h1>
            <p>
                {pokemonSpecies.generation} - {pokemonSpecies.genus}
            </p>

            <Tabs defaultActiveKey="info" className="mb-3">
                <Tab eventKey="info" title="Info">
                    <PokemonBasicInfo form={pokemonForm} species={pokemonSpecies} />
                </Tab>
                <Tab eventKey="learnset" title="Learnset">
                    <PokemonLearnset moves={pokemonLearnsets}/>
                </Tab>
                <Tab eventKey="locations" title="Locations">
                    <PokemonLocations />
                </Tab>
                <Tab eventKey="sprites" title="Sprites">
                    <PokemonSprites sprites={pokemonForm.sprites} />
                </Tab>
                <Tab eventKey="entries" title="Entries">
                    <PokemonEntries entries={pokemonSpecies.flavor_text_entries} />
                </Tab>
            </Tabs>
        </Container>
    );
};

export default PokemonDetail;
