import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SortableTable from './SortableTable';
import { Link } from 'react-router-dom';

interface Region {
    id: number;
    name: string;
    population: number;
}

const RegionList: React.FC = () => {
    const [regions, setRegions] = useState<Region[]>([]);

    useEffect(() => {
        const fetchRegions = async () => {
            try {
                const response = await axios.get('http://localhost:5000/regions');
                setRegions(response.data.data);
            } catch (error) {
                console.error('Error fetching regions:', error);
            }
        };

        fetchRegions();
    }, []);

    const columns: Array<{ key: keyof Region, label: string, render?: (item: Region) => React.ReactNode }> = [
        { 
            key: 'name', 
            label: 'Name', 
            render: (item: Region) => <Link to={`/regions/${item.id}`} className="text-decoration-none text-dark link-underline-opacity-75-hover">{item.name}</Link>
        },
        { key: 'population', label: 'Population' },
    ];

    return (
        <div>
            <h1>Regions</h1>
            <SortableTable columns={columns} data={regions} />
        </div>
    );
};

export default RegionList;
