import React, { useState, useEffect } from 'react';
import { Table, ButtonGroup, Button } from 'react-bootstrap';
import { PokemonMove, PokemonLearnsetProps, GenericMoveDetails } from '../../types';
import axios from 'axios';
import SortableTable from '../SortableTable';

// versionGroup -> generation mapping
const versionGroupToGenerationMap: { [key: string]: string } = {
    '1': 'Generation 1',
    '2': 'Generation 1',
    '3': 'Generation 2',
    '4': 'Generation 2',
    '5': 'Generation 3',
    '6': 'Generation 3',
    '7': 'Generation 3',
    '12': 'Generation 3',
    '13': 'Generation 3',
    '8': 'Generation 4',
    '9': 'Generation 4',
    '10': 'Generation 4',
    '11': 'Generation 5',
    '14': 'Generation 5',
    '15': 'Generation 6',
    '16': 'Generation 6',
    '17': 'Generation 7',
    '18': 'Generation 7',
    '19': 'Generation 7',
    '20': 'Generation 8',
    '21': 'Generation 8',
    '22': 'Generation 8',
    '23': 'Generation 8',
    '24': 'Generation 8',
    '25': 'Generation 9',
    '26': 'Generation 9',
    '27': 'Generation 9',
};

// Group moves by generation and version group
const groupMovesByGenerationAndVersion = (moves: PokemonMove[]) => {
    const groupedByGeneration: { [generation: string]: { [versionGroup: string]: { [learnMethod: string]: PokemonMove[] } } } = {};

    moves.forEach((move) => {
        move.version_group_details.forEach((details) => {
            const versionId = details.version_group.url.split('/').slice(-2, -1)[0];
            const generation = versionGroupToGenerationMap[versionId];

            if (!generation) return;

            const versionGroup = details.version_group.name;
            const learnMethod = details.move_learn_method.name;

            if (!groupedByGeneration[generation]) {
                groupedByGeneration[generation] = {};
            }
            if (!groupedByGeneration[generation][versionGroup]) {
                groupedByGeneration[generation][versionGroup] = {};
            }
            if (!groupedByGeneration[generation][versionGroup][learnMethod]) {
                groupedByGeneration[generation][versionGroup][learnMethod] = [];
            }

            groupedByGeneration[generation][versionGroup][learnMethod].push({
                move: move.move,
                version_group_details: [details],
            });
        });
    });

    return groupedByGeneration;
};

const PokemonLearnset: React.FC<PokemonLearnsetProps> = ({ moves }) => {
    //defaults
    const [activeGeneration, setActiveGeneration] = useState<string>('Generation 9');
    const [activeVersionGroup, setActiveVersionGroup] = useState<string>('scarlet-violet');
    const [moveDetails, setMoveDetails] = useState<{ [key: string]: GenericMoveDetails }>({});

    const groupedMoves = groupMovesByGenerationAndVersion(moves);

    useEffect(() => {
        const fetchMoveDetails = async (move: PokemonMove) => {
            try {
                const { data: moveData } = await axios.get(move.move.url);

                const moveDetail: GenericMoveDetails = {
                    name: moveData.name,
                    power: moveData.power,
                    accuracy: moveData.accuracy,
                    pp: moveData.pp,
                    damage_class: moveData.damage_class,
                    type: moveData.type,
                    level_learned_at: move.version_group_details[0].level_learned_at,
                    machine: '',
                    learn_method: move.version_group_details[0].move_learn_method.name,
                };

                console.log(move.version_group_details[0].move_learn_method.name);
                if (move.version_group_details[0].move_learn_method.name === 'machine') {
                    const machine = moveData.machines.find(
                        (machine: any) => machine.version_group.name === activeVersionGroup
                    );
                    if (machine) {
                        const { data: machineData } = await axios.get(machine.machine.url);
                        moveDetail.machine = machineData.item.name;
                    }
                }
                const uniqueKey = `${move.move.name}-${move.version_group_details[0].move_learn_method.name}`;
                setMoveDetails(prevDetails => ({ ...prevDetails, [move.move.name]: moveDetail }));
            } catch (error) {
                console.error('Failed to fetch move details:', error);
            }
        };

        // Fetch details for moves not yet stored in state
        moves.forEach((move) => {
            if (!moveDetails[move.move.name]) {
                fetchMoveDetails(move);
            }
        });
    }, [moves, moveDetails, activeVersionGroup]);


    const renderMovesTable = (learnMethod: string, moves: PokemonMove[], title: string, fallbackMessage: string) => {
        const columns: Array<{ key: keyof GenericMoveDetails, label: string, render?: (item: GenericMoveDetails) => React.ReactNode }> = [
            { key: 'name', label: 'Move' },
            { key: 'type', label: 'Type', render: (item: GenericMoveDetails) => item.type.name },
            { key: 'power', label: 'Power' },
            { key: 'accuracy', label: 'Accuracy' },
            { key: 'pp', label: 'PP' },
            { key: 'damage_class', label: 'Category', render: (item: GenericMoveDetails) => item.damage_class.name }
        ];

        if (learnMethod === 'level-up') {
            columns.push({ key: 'level_learned_at', label: 'Level Learned' });
        }

        if (learnMethod === 'machine') {
            columns.push({ key: 'machine', label: 'Machine' });
        }

        const data: GenericMoveDetails[] = moves
            .map(move => moveDetails[move.move.name])
            .filter(Boolean);

        return (
            <>
                <h4>{title}</h4>
                {data.length > 0 ? (
                    <SortableTable
                        columns={columns}
                        data={data}
                    />
                ) : (
                    <p>{fallbackMessage}</p>
                )}
            </>
        );
    };

    const renderVersionGroupLinks = (versionGroups: { [versionGroup: string]: { [learnMethod: string]: PokemonMove[] } }) => (
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '20px' }}>
            <ButtonGroup>
                {Object.keys(versionGroups).map((versionGroup) => (
                    <Button
                        key={versionGroup}
                        variant={versionGroup === activeVersionGroup ? 'primary' : 'light'}
                        onClick={() => setActiveVersionGroup(versionGroup)}
                    >
                        {versionGroup.charAt(0).toUpperCase() + versionGroup.slice(1)}
                    </Button>
                ))}
            </ButtonGroup>
        </div>
    );

    const renderGenerationContent = (generation: string, versionGroups: { [versionGroup: string]: { [learnMethod: string]: PokemonMove[] } }) => {
        const versionGroupContent = versionGroups[activeVersionGroup] || {};
        const levelUpMoves = versionGroupContent['level-up'] || [];
        const evolutionMoves = levelUpMoves.filter(move => move.version_group_details[0].level_learned_at === 0);
        const remainingLevelUpMoves = levelUpMoves.filter(move => move.version_group_details[0].level_learned_at !== 0);

        return (
            <div>
                {renderVersionGroupLinks(versionGroups)}
                <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <div style={{ flex: 1 }}>
                        {remainingLevelUpMoves.length > 0 && renderMovesTable('level-up', remainingLevelUpMoves, 'Level-Up Moves', '')}
                        {evolutionMoves.length > 0 && renderMovesTable('evolution', evolutionMoves, 'Moves Learned on Evolution', '')}
                        {renderMovesTable('tutor', versionGroupContent['tutor'] || [], 'Move Tutor', 'Does not learn any moves by tutor')}
                        {renderMovesTable('egg', versionGroupContent['egg'] || [], 'Egg Moves', 'Does not learn any moves by egg')}
                    </div>
                    <div style={{ flex: 1 }}>
                        {renderMovesTable('machine', versionGroupContent['machine'] || [], 'Machine Moves', 'Does not learn any moves by machine')}

                    </div>
                </div>
            </div>
        );
    };

        const renderGenerationLinks = () => {
        const sortedGenerations = Object.keys(groupedMoves).sort();

        return (
            <div style={{ marginBottom: '20px', textAlign: 'center' }}>
                {sortedGenerations.map((generation, index) => {
                    const generationNumber = generation.replace('Generation ', '');
                    return (
                        <React.Fragment key={index}>
                            <span
                                onClick={() => {
                                    setActiveGeneration(generation);
                                    const firstVersionGroup = Object.keys(groupedMoves[generation])[0];
                                    setActiveVersionGroup(firstVersionGroup);
                                }}
                                style={{
                                    cursor: 'pointer',
                                    fontWeight: activeGeneration === generation ? 'bold' : 'normal',
                                    color: activeGeneration === generation ? 'black' : '#007bff',
                                }}
                            >
                                {generationNumber}
                            </span>
                            {index < sortedGenerations.length - 1 && <span style={{ margin: '0 10px' }}>|</span>}
                        </React.Fragment>
                    );
                })}
            </div>
        );
    };

    return (
        <div>
            {renderGenerationLinks()}
            {renderGenerationContent(activeGeneration, groupedMoves[activeGeneration])}
        </div>
    );
};

export default PokemonLearnset;
