import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery, useMutation } from '@tanstack/react-query';
import axios from 'axios';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';

const fetchFieldRatings = async (id: string) => {
    const { data } = await axios.get(`http://localhost:5000/trainers/${id}`);
    return data.data.field_rating;
};

const updateFieldRating = async ({ id, updatedFieldRatings }: { id: string, updatedFieldRatings: any }) => {
    const { data } = await axios.put(`http://localhost:5000/trainers/${id}/field_ratings`, updatedFieldRatings);
    return data;
}

const FieldRatingEdit: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();

    const { data: fieldRatings, isLoading } = useQuery(['field_ratings', id], () => fetchFieldRatings(id!));

    const [updatedRatings, setUpdatedRatings] = useState(fieldRatings);

    const mutation = useMutation({
        mutationFn: updateFieldRating,
        onSuccess: () => {
            navigate(`/trainers/${id}`);
        },
        onError: (error) => {
            console.error('Failed to update field ratings', error);
        },
    });

    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setUpdatedRatings({ ...updatedRatings, [name]: Number(value) });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        mutation.mutate(updatedRatings);
    };

    if (isLoading || !fieldRatings) return <div>Loading...</div>;

    return (
        <Container className="mt-4">
            <h1>Edit Field Ratings</h1>
            <Form onSubmit={handleSubmit}>
                <Row>
                    {Object.keys(fieldRatings).map((key) => (
                        <Col key={key} md={6} className="mb-3">
                            <Form.Group>
                                <Form.Label>{key.replace(/_/g, ' ').replace('field rating', '').trim()}</Form.Label>
                                <Form.Control
                                    type="number"
                                    name={key}
                                    value={updatedRatings[key] || ''}
                                    onChange={handleInputChange}
                                />
                            </Form.Group>
                        </Col>
                    ))}
                </Row>
                <Button type="submit" variant="primary">
                    Save Changes
                </Button>
            </Form>
        </Container>
    );
};

export default FieldRatingEdit;
