import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Pagination } from 'react-bootstrap';

const TrainerList: React.FC = () => {
    const [trainers, setTrainers] = useState<any[]>([]);
    const [currentPage, setCurrentPage] = useState<number>(1);
    const [trainersPerPage] = useState<number>(50); 

    useEffect(() => {
        const fetchTrainers = async () => {
            try {
                const response = await axios.get('http://localhost:5000/trainers');
                setTrainers(response.data.data);
            } catch (error) {
                console.error('Error fetching trainers:', error);
            }
        };

        fetchTrainers();
    }, []);

    // Get current trainers
    const indexOfLastTrainer = currentPage * trainersPerPage;
    const indexOfFirstTrainer = indexOfLastTrainer - trainersPerPage;
    const currentTrainers = trainers.slice(indexOfFirstTrainer, indexOfLastTrainer);

    // Change page
    const paginate = (pageNumber: number) => setCurrentPage(pageNumber);

    // Calculate total pages
    const totalPages = Math.ceil(trainers.length / trainersPerPage);

    return (
        <div>
            <h1>Active Trainers</h1>
            <table className="table">
                <thead>
                    <tr>
                        <th scope="col">Rank</th>
                        <th scope="col">Name</th>
                        <th scope="col">Rating</th>
                        <th scope="col">Region</th>
                    </tr>
                </thead>
                <tbody>
                    {currentTrainers.map((trainer, index) => (
                        <tr key={trainer.id}>
                            <td>{indexOfFirstTrainer + index + 1}</td>
                            <td>{trainer.fname} {trainer.lname}</td>
                            <td>{trainer.pwtr_rating.toFixed(2)}</td>
                            <td>{trainer.region_name}</td>
                        </tr>
                    ))}
                </tbody>
            </table>

            <Pagination>
                <Pagination.First onClick={() => paginate(1)} disabled={currentPage === 1} />
                <Pagination.Prev onClick={() => paginate(currentPage - 1)} disabled={currentPage === 1} />
                {[...Array(totalPages)].map((_, i) => (
                    <Pagination.Item key={i + 1} active={i + 1 === currentPage} onClick={() => paginate(i + 1)}>
                        {i + 1}
                    </Pagination.Item>
                ))}
                <Pagination.Next onClick={() => paginate(currentPage + 1)} disabled={currentPage === totalPages} />
                <Pagination.Last onClick={() => paginate(totalPages)} disabled={currentPage === totalPages} />
            </Pagination>
        </div>
    );
};

export default TrainerList;
