import React, { useState } from 'react';
import { Tabs, Tab, Table } from 'react-bootstrap';
import { PokemonMove } from '../../types';

interface PokemonLearnsetProps {
    moves: PokemonMove[];
}

const groupMovesByGenerationAndVersion = (moves: PokemonMove[]) => {
    const groupedByGeneration: { [generation: string]: { [versionGroup: string]: { [learnMethod: string]: PokemonMove[] } } } = {};

    moves.forEach((move) => {
        move.version_group_details.forEach((details) => {
            const versionId = details.version_group.url.split('/').slice(-2, -1)[0];
            const generationId = Math.ceil(Number(versionId) / 3); // assuming each generation has roughly 3 version groups

            const generation = `Generation ${generationId}`;
            const versionGroup = details.version_group.name;
            const learnMethod = details.move_learn_method.name;

            if (!groupedByGeneration[generation]) {
                groupedByGeneration[generation] = {};
            }
            if (!groupedByGeneration[generation][versionGroup]) {
                groupedByGeneration[generation][versionGroup] = {};
            }
            if (!groupedByGeneration[generation][versionGroup][learnMethod]) {
                groupedByGeneration[generation][versionGroup][learnMethod] = [];
            }

            groupedByGeneration[generation][versionGroup][learnMethod].push({
                move: move.move,
                version_group_details: [details],
            });
        });
    });

    return groupedByGeneration;
};

const PokemonLearnset: React.FC<PokemonLearnsetProps> = ({ moves }) => {
    const [activeGeneration, setActiveGeneration] = useState<string>('Generation 1');
    const [activeVersionGroup, setActiveVersionGroup] = useState<string>('');

    const groupedMoves = groupMovesByGenerationAndVersion(moves);

    const renderMovesTable = (learnMethod: string, moves: PokemonMove[]) => (
        <Table bordered>
            <thead>
                <tr>
                    <th>Move</th>
                    <th>Level</th>
                </tr>
            </thead>
            <tbody>
                {moves.map((move, index) => (
                    <tr key={index}>
                        <td>{move.move.name.charAt(0).toUpperCase() + move.move.name.slice(1)}</td>
                        <td>{move.version_group_details[0].level_learned_at}</td>
                    </tr>
                ))}
            </tbody>
        </Table>
    );

    const renderVersionGroupTabs = (versionGroups: { [versionGroup: string]: { [learnMethod: string]: PokemonMove[] } }) => (
        <Tabs activeKey={activeVersionGroup} onSelect={(k) => setActiveVersionGroup(k || '')} className="mb-3">
            {Object.entries(versionGroups).map(([versionGroup, learnMethods]) => (
                <Tab eventKey={versionGroup} title={versionGroup} key={versionGroup}>
                    <div style={{ display: 'flex', justifyContent: 'space-between' }}>
                        <div style={{ flex: 1 }}>
                            {learnMethods['level-up'] && (
                                <>
                                    <h4>Level-Up Moves</h4>
                                    {renderMovesTable('level-up', learnMethods['level-up'])}
                                </>
                            )}
                        </div>
                        <div style={{ flex: 1 }}>
                            {learnMethods['machine'] && (
                                <>
                                    <h4>Machine Moves</h4>
                                    {renderMovesTable('machine', learnMethods['machine'])}
                                </>
                            )}
                        </div>
                    </div>
                </Tab>
            ))}
        </Tabs>
    );

    return (
        <Tabs activeKey={activeGeneration} onSelect={(k) => setActiveGeneration(k || 'Generation 1')} className="mb-3">
            {Object.entries(groupedMoves).map(([generation, versionGroups]) => (
                <Tab eventKey={generation} title={generation} key={generation}>
                    {renderVersionGroupTabs(versionGroups)}
                </Tab>
            ))}
        </Tabs>
    );
};

export default PokemonLearnset;
