import React, { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';
import { useQuery, useMutation } from '@tanstack/react-query';
import axios from 'axios';
import { Container, Row, Col, Form, Button, Dropdown } from 'react-bootstrap';

// Fetch field ratings from the server
const fetchFieldRatings = async (id: string) => {
    const { data } = await axios.get(`http://localhost:5000/trainers/${id}`);
    return data.data.field_rating;
};

// Update field ratings on the server
const updateFieldRating = async ({ id, updatedFieldRatings }: { id: string; updatedFieldRatings: any }) => {
    const { data } = await axios.put(`http://localhost:5000/trainers/${id}/field_ratings`, updatedFieldRatings);
    return data;
};

const FieldRatingEdit: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();

    // Fetch field ratings using useQuery
    const { data: fieldRatings, isLoading } = useQuery({
        queryKey: ['field_ratings', id],
        queryFn: () => fetchFieldRatings(id!),
    });

    // Initialize state with fetched field ratings
    const [updatedRatings, setUpdatedRatings] = useState<any>(null);

    // Initialize the state with field ratings when data is available
    useEffect(() => {
        if (fieldRatings) {
            setUpdatedRatings(fieldRatings);
        }
    }, [fieldRatings]);

    // Mutation to handle updating field ratings
    const mutation = useMutation({
        mutationFn: updateFieldRating,
        onSuccess: () => {
            navigate(`/trainers/${id}`);
        },
        onError: (error) => {
            console.error('Failed to update field ratings', error);
        },
    });

    // Handle input changes
    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setUpdatedRatings({ ...updatedRatings, [name]: Number(value) });
    };

    // Handle form submission
    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (updatedRatings) {
            mutation.mutate({ id: id!, updatedFieldRatings: updatedRatings });
        }
    };

    if (isLoading) return <div>Loading...</div>;

    return (
        <Container className="mt-4">
            <Row className="align-items-center mb-3">
                <Col>
                    <h1>Edit Field Ratings</h1>
                </Col>
                <Col xs="auto" className="text-end">
                    <Dropdown className="float-end">
                        <Dropdown.Toggle variant="secondary" id="dropdown-basic">
                            Edit
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item as={Link} to={`/trainers/${id}`}>Overview</Dropdown.Item>
                            <Dropdown.Item as={Link} to={`/trainers/${id}/pokemon`}>Pokemon</Dropdown.Item>
                            <Dropdown.Item as={Link} to={`/trainers/${id}/ratings`}>Past Ratings</Dropdown.Item>
                            <Dropdown.Item as={Link} to={`/edit_trainer/${id}`}>Edit</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Col>
            </Row>

            <Form onSubmit={handleSubmit}>
                <Row>
                    {updatedRatings &&
                        Object.keys(updatedRatings).map((key) => (
                            key !== 'id' && key !== 'rating_id' && (
                                <Col key={key} md={2} className="mb-3">
                                    <Form.Group>
                                        <Form.Label>{key.replace(/_/g, ' ').replace('field rating', '').trim()}</Form.Label>
                                        <Form.Control
                                            type="number"
                                            name={key}
                                            value={updatedRatings[key] || ''}
                                            onChange={handleInputChange}
                                        />
                                    </Form.Group>
                                </Col>
                            )
                        ))}
                </Row>
                <Button type="submit" variant="primary">
                    Save Changes
                </Button>
            </Form>

            <Row className="mt-3">
                <Col xs="auto" className="text-end">
                    <Dropdown className="float-end">
                        <Dropdown.Toggle variant="secondary" id="dropdown-basic">
                            Go to Other Edits
                        </Dropdown.Toggle>

                        <Dropdown.Menu>
                            <Dropdown.Item as={Link} to={`/edit_trainer/${id}/field_ratings`}>Edit Field Ratings</Dropdown.Item>
                            <Dropdown.Item as={Link} to={`/edit_trainer/${id}`}>Edit Trainer</Dropdown.Item>
                        </Dropdown.Menu>
                    </Dropdown>
                </Col>
            </Row>
        </Container>
    );
};

export default FieldRatingEdit;
