import React from 'react';
import { BrowserRouter as Router, Route, Routes, useLocation } from 'react-router-dom';
import Trainers from './routes/Trainers';
import InactiveTrainers from './routes/InactiveTrainers';
import Sidebar from './components/Sidebar';
import Navbar from './components/Navbar';
import './App.scss';

const App: React.FC = () => {
    const location = useLocation();
    const showSidebar = location.pathname.startsWith('/trainers');

    return (
        <div className="app-container">
            <Navbar />
            <div className="main-content">
              {showSidebar && <Sidebar />}
              <div className={`content ${showSidebar ? 'with-sidebar' : ''}`}>
                  <Routes>
                      <Route path="/trainers/inactive" element={<InactiveTrainers />} />
                      <Route path="/trainers" element={<Trainers />} />
                      <Route path="/" element={<div><h1>Welcome to the Trainer Management System</h1></div>} />
                  </Routes>
              </div>
            </div>
        </div>
    );
};

export default App;
