import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Card, Container, Row, Col, Spinner, Pagination } from 'react-bootstrap';

interface PokemonSpecies {
    name: string;
    url: string;
    sprite?: string;
}

const Pokedex: React.FC = () => {
    const [pokemonSpecies, setPokemonSpecies] = useState<PokemonSpecies[]>([]);
    const [nextUrl, setNextUrl] = useState<string | null>(null);
    const [prevUrl, setPrevUrl] = useState<string | null>(null);
    const [count, setCount] = useState<number>(0);
    const [loading, setLoading] = useState<boolean>(true);

    useEffect(() => {
        fetchPokemonSpecies('https://pokeapi.co/api/v2/pokemon-species?offset=0&limit=20');
    }, []);

    const fetchPokemonSpecies = async (url: string) => {
        setLoading(true);
        try {
            const response = await axios.get(url);
            const speciesResults = response.data.results;

            const speciesWithSprites = await Promise.all(speciesResults.map(async (species: PokemonSpecies) => {
                const speciesData = await axios.get(species.url);
                const defaultVariety = speciesData.data.varieties.find((variety: any) => variety.is_default);

                if (defaultVariety) {
                    const pokemonData = await axios.get(defaultVariety.pokemon.url);
                    return {
                        name: species.name,
                        url: species.url,
                        sprite: pokemonData.data.sprites.front_default,
                    };
                } else {
                    return {
                        name: species.name,
                        url: species.url,
                    };
                }
            }));

            setPokemonSpecies(speciesWithSprites);
            setNextUrl(response.data.next);
            setPrevUrl(response.data.previous);
            setCount(response.data.count);
        } catch (error) {
            console.error('Error fetching Pokémon species:', error);
        }
        setLoading(false);
    };

    return (
        <Container>
            <h1>Pokedex</h1>
            {loading && <Spinner animation="border" />}
            <Row>
                {pokemonSpecies.map((species, index) => (
                    <Col md={3} sm={6} key={index} className="mb-4">
                        <Card>
                            <Card.Img variant="top" src={species.sprite || 'https://via.placeholder.com/150'} alt={species.name} />
                            <Card.Body>
                                <Card.Title>{species.name.charAt(0).toUpperCase() + species.name.slice(1)}</Card.Title>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            <Pagination className="justify-content-center mt-3">
                {prevUrl && (
                    <Pagination.Prev onClick={() => fetchPokemonSpecies(prevUrl)}>
                        Previous
                    </Pagination.Prev>
                )}
                {nextUrl && (
                    <Pagination.Next onClick={() => fetchPokemonSpecies(nextUrl)}>
                        Next
                    </Pagination.Next>
                )}
            </Pagination>
        </Container>
    );
};

export default Pokedex;
