import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Button, Card, Container, Row, Col, Spinner, Pagination } from 'react-bootstrap';

interface PokemonSpecies {
    name: string;
    url: string;
    sprite?: string;
}

const Pokedex: React.FC = () => {
    const [pokemonSpecies, setPokemonSpecies] = useState<PokemonSpecies[]>([]);
    const [nextUrl, setNextUrl] = useState<string | null>(null);
    const [prevUrl, setPrevUrl] = useState<string | null>(null);
    const [currentPage, setCurrentPage] = useState<number>(1);
    const [totalPages, setTotalPages] = useState<number>(1);
    const [loading, setLoading] = useState<boolean>(true);
    const limit = 20; // Number of Pokémon per page

    useEffect(() => {
        fetchPokemonSpecies('https://pokeapi.co/api/v2/pokemon-species?offset=0&limit=20', 1);
    }, []);

    const fetchPokemonSpecies = async (url: string, page: number) => {
        setLoading(true);
        try {
            const response = await axios.get(url);
            const speciesResults = response.data.results;

            const speciesWithSprites = await Promise.all(speciesResults.map(async (species: PokemonSpecies) => {
                const speciesData = await axios.get(species.url);
                const defaultVariety = speciesData.data.varieties.find((variety: any) => variety.is_default);

                if (defaultVariety) {
                    const pokemonData = await axios.get(defaultVariety.pokemon.url);
                    return {
                        name: species.name,
                        url: species.url,
                        sprite: pokemonData.data.sprites.front_default,
                    };
                } else {
                    return {
                        name: species.name,
                        url: species.url,
                    };
                }
            }));

            setPokemonSpecies(speciesWithSprites);
            setNextUrl(response.data.next);
            setPrevUrl(response.data.previous);
            setTotalPages(Math.ceil(response.data.count / limit));
            setCurrentPage(page);
        } catch (error) {
            console.error('Error fetching Pokémon species:', error);
        }
        setLoading(false);
    };

    const handlePageClick = (page: number) => {
        const offset = (page - 1) * limit;
        const url = `https://pokeapi.co/api/v2/pokemon-species?offset=${offset}&limit=${limit}`;
        fetchPokemonSpecies(url, page);
    };

    return (
        <Container>
            <h1>Pokedex</h1>
            {loading && <Spinner animation="border" />}
            <Row>
                {pokemonSpecies.map((species, index) => (
                    <Col xs={6} sm={4} md={3} lg={2} key={index} className="mb-4">
                        <Card className="text-center">
                            <Card.Img variant="top" src={species.sprite || 'https://via.placeholder.com/96'} alt={species.name} style={{ width: '80px', height: '80px', margin: 'auto' }} />
                            <Card.Body>
                                <Card.Title style={{ fontSize: '1rem' }}>{species.name.charAt(0).toUpperCase() + species.name.slice(1)}</Card.Title>
                            </Card.Body>
                        </Card>
                    </Col>
                ))}
            </Row>
            <Pagination className="justify-content-center mt-3">
                {prevUrl && (
                    <Pagination.Prev onClick={() => handlePageClick(currentPage - 1)} disabled={currentPage === 1}>
                        Previous
                    </Pagination.Prev>
                )}
                {[...Array(totalPages)].map((_, i) => (
                    <Pagination.Item key={i + 1} active={i + 1 === currentPage} onClick={() => handlePageClick(i + 1)}>
                        {i + 1}
                    </Pagination.Item>
                ))}
                {nextUrl && (
                    <Pagination.Next onClick={() => handlePageClick(currentPage + 1)} disabled={currentPage === totalPages}>
                        Next
                    </Pagination.Next>
                )}
            </Pagination>
        </Container>
    );
};

export default Pokedex;
