import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import SortableTable from './SortableTable';

interface City {
    name: string;
    population: number;
}

interface Region {
    name: string;
    population: number;
    cities: City[];
}

const RegionDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [region, setRegion] = useState<Region | null>(null);
    const [currentPage, setCurrentPage] = useState<number>(1);
    const citiesPerPage = 10;

    useEffect(() => {
        const fetchRegion = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/regions/${id}`);
                setRegion(response.data.data);
            } catch (error) {
                console.error('Error fetching region:', error);
            }
        };

        fetchRegion();
    }, [id]);

    if (!region) {
        return <div>Loading...</div>;
    }

    const columns: Array<{ key: keyof City, label: string, render?: (item: City) => React.ReactNode }> = [
        { key: 'name', label: 'City Name' },
        { key: 'population', label: 'Population' },
    ];

    return (
        <div>
            <h1>{region.name}</h1>
            <p>Population: {region.population.toLocaleString()}</p>
            <h2>Cities</h2>
            <SortableTable 
                columns={columns} 
                data={region.cities} 
                currentPage={currentPage}
                itemsPerPage={trainersPerPage}
            />
        </div>
    );
};

export default RegionDetail;
