import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SortableTable from './SortableTable';
import { Pagination } from 'react-bootstrap';

interface Trainer {
    id: number;
    fname: string;
    lname: string;
    pwtr_rating: number;
    region_name: string;
    title: string;
}

const TrainerList: React.FC = () => {
    const [trainers, setTrainers] = useState<Trainer[]>([]);
    const [currentPage, setCurrentPage] = useState<number>(1);
    const trainersPerPage = 20;

    useEffect(() => {
        const fetchTrainers = async () => {
            try {
                const response = await axios.get('http://localhost:5000/trainers');
                setTrainers(response.data.data);
            } catch (error) {
                console.error('Error fetching trainers:', error);
            }
        };

        fetchTrainers();
    }, []);

    const columns: Array<{ key: "custom" | keyof Trainer, label: string, render?: (item: Trainer, index?: number) => React.ReactNode }> = [
        {
            key: 'custom',
            label: 'Rank',
            render: (_, index) => (index !== undefined ? index + 1 : ''),
        },
        { key: 'fname', label: 'First Name' },
        { key: 'lname', label: 'Last Name' },
        { 
            key: 'pwtr_rating', 
            label: 'Rating', 
            render: (item: Trainer) => item.pwtr_rating.toFixed(2)
        },
        { key: 'region_name', label: 'Region' },
        {
            key: 'title',
            label: 'Title',
            render: (item: Trainer) => (item.title !== 'None' ? item.title : ''),
        },
    ];

    const totalPages = Math.ceil(trainers.length / trainersPerPage);

    return (
        <div>
            <h1>PWTR List</h1>
            <SortableTable
                columns={columns}
                data={trainers}
                currentPage={currentPage}
                itemsPerPage={trainersPerPage}
            />
            <Pagination>
                <Pagination.First onClick={() => setCurrentPage(1)} disabled={currentPage === 1} />
                <Pagination.Prev onClick={() => setCurrentPage(currentPage - 1)} disabled={currentPage === 1} />
                {[...Array(totalPages)].map((_, i) => (
                    <Pagination.Item key={i + 1} active={i + 1 === currentPage} onClick={() => setCurrentPage(i + 1)}>
                        {i + 1}
                    </Pagination.Item>
                ))}
                <Pagination.Next onClick={() => setCurrentPage(currentPage + 1)} disabled={currentPage === totalPages} />
                <Pagination.Last onClick={() => setCurrentPage(totalPages)} disabled={currentPage === totalPages} />
            </Pagination>
        </div>
    );
};

export default TrainerList;
