import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { useQuery, useMutation } from '@tanstack/react-query';
import axios from 'axios';
import { Container, Row, Col, Form, Button } from 'react-bootstrap';
import { FieldRating } from '../../types';

const fetchFieldRatings = async (id: string): Promise<FieldRating> => {
    const { data } = await axios.get(`http://localhost:5000/trainers/${id}`);
    return data.data.field_rating;
};

const updateFieldRating = async ({ id, updatedFieldRatings }: { id: string; updatedFieldRatings: FieldRating }) => {
    const { data } = await axios.put(`http://localhost:5000/trainers/${id}/field_ratings`, updatedFieldRatings);
    return data;
};

const FieldRatingEdit: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const navigate = useNavigate();

    // Fetch field ratings using useQuery
    const { data: fieldRatings, isLoading } = useQuery<FieldRating>(['field_ratings', id], () => fetchFieldRatings(id!));

    // Create state to track updated field ratings
    const [updatedRatings, setUpdatedRatings] = useState<FieldRating | null>(null);

    useEffect(() => {
        if (fieldRatings) {
            setUpdatedRatings(fieldRatings); // Initialize updatedRatings state when data is fetched
        }
    }, [fieldRatings]);

    // Mutation to handle updating field ratings
    const mutation = useMutation({
        mutationFn: updateFieldRating,
        onSuccess: () => {
            navigate(`/trainers/${id}`);
        },
        onError: (error) => {
            console.error('Failed to update field ratings', error);
        },
    });

    // Handle input changes
    const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        if (updatedRatings) {
            setUpdatedRatings({ ...updatedRatings, [name]: Number(value) });
        }
    };

    // Handle form submission
    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        if (updatedRatings) {
            mutation.mutate({ id: id!, updatedFieldRatings: updatedRatings });
        }
    };

    if (isLoading || !updatedRatings) return <div>Loading...</div>;

    return (
        <Container className="mt-4">
            <h1>Edit Field Ratings</h1>
            <Form onSubmit={handleSubmit}>
                <Row>
                    {Object.keys(updatedRatings).map((key) => (
                        key !== 'id' && key !== 'rating_id' && (
                            <Col key={key} md={6} className="mb-3">
                                <Form.Group>
                                    <Form.Label>{key.replace(/_/g, ' ').replace('field rating', '').trim()}</Form.Label>
                                    <Form.Control
                                        type="number"
                                        name={key}
                                        value={updatedRatings[key as keyof FieldRating] || ''}
                                        onChange={handleInputChange}
                                    />
                                </Form.Group>
                            </Col>
                        )
                    ))}
                </Row>
                <Button type="submit" variant="primary">
                    Save Changes
                </Button>
            </Form>
        </Container>
    );
};

export default FieldRatingEdit;
