import React from 'react';
import { useParams, Link } from 'react-router-dom';
import { useQuery } from '@tanstack/react-query';
import axios from 'axios';
import { Container, Row, Col, Image, OverlayTrigger, Tooltip } from 'react-bootstrap';
import { TrainerData } from '../../types';

const fetchTrainer = async (id: string) => {
    const { data } = await axios.get(`http://localhost:5000/trainers/${id}`);
    return data.data;
};

const TrainerDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const { data: trainerData, isLoading } = useQuery<TrainerData>({
        queryKey: ['trainer', id],
        queryFn: () => fetchTrainer(id!),
    });

    if (isLoading) return <div>Loading...</div>;

    const trainer = trainerData!.trainer;

    return (
        <Container className="mt-4">
            <Row className="align-items-center">
                <Col xs={12} md={4}>
                    <Image 
                        src="path_to_placeholder_image.jpg" 
                        roundedCircle 
                        alt={`${trainer.fname} ${trainer.lname}`} 
                        fluid 
                    />
                </Col>
                <Col xs={12} md={8}>
                    <h1>{trainer.fname} {trainer.lname}</h1>
                    <p>
                        Birthdate: {trainer.birthdate || 'Unknown'}
                    </p>
                    <p>
                        PWTR Rating: 
                        <OverlayTrigger
                            placement="top"
                            overlay={
                                <Tooltip>
                                    Peak Rating: {trainer.peak_rating || 'N/A'}
                                </Tooltip>
                            }
                        >
                            <span> {trainer.pwtr_rating}</span>
                        </OverlayTrigger>
                    </p>
                    <p>
                        Rank: 
                        <OverlayTrigger
                            placement="top"
                            overlay={
                                <Tooltip>
                                    Peak Rank: {trainer.peak_rank || 'N/A'}
                                </Tooltip>
                            }
                        >
                            <span> {trainer.rank}</span>
                        </OverlayTrigger>
                    </p>
                    <p>
                        Region: 
                        <Link to={`/regions/${trainer.region_id}`}>
                            {trainer.region_name}
                        </Link>
                    </p>
                    <p>Title: {trainer.title || 'None'}</p>
                    <p>Hometowns:</p>
                    <ul>
                        {trainer.hometowns.map((hometown, index) => (
                            <li key={index}>
                                <Link to={`/regions/${hometown.region_id}`}>
                                    {hometown.region_name}
                                </Link>{' '}
                                -{' '}
                                <Link to={`/cities/${hometown.city_id}`}>
                                    {hometown.city_name}
                                </Link>
                            </li>
                        ))}
                    </ul>
                </Col>
            </Row>
        </Container>
    );
};

export default TrainerDetail;
