import React from 'react';
import { Row, Col } from 'react-bootstrap';
import '../css/Pokedex.scss'

interface Ability {
    name: string;
    is_hidden: boolean;
}

interface Stat {
    name: string;
    base_stat: number;
}

interface Type {
    name: string;
}

interface PokemonForm {
    id: number;
    name: string;
    abilities: Ability[];
    height: number;
    weight: number;
    stats: Stat[];
    types: Type[];
}

interface PokemonSpecies {
    name: string;
    nationalDexNumber: number;
    generation: string;
    genus: string;
    varieties: any[];
    evolution_chain: EvolutionChainLink;
}

interface PokemonBasicInfoProps {
    form: PokemonForm;
    species: PokemonSpecies;
}

interface EvolutionChainLink {
    species: {
        name: string;
        url: string;
    };
    evolves_to: EvolutionChainLink[];
}

const PokemonBasicInfo: React.FC<PokemonBasicInfoProps> = ({ form, species }) => {
    const renderVarieties = () => {
        return species.varieties.map((variety) => (
            <div key={variety.pokemon.url} className="variety-item">
                <img
                    src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${variety.pokemon.url.split('/').slice(-2, -1)}.png`}
                    alt={variety.pokemon.name}
                    style={{ width: '35px' }}
                />
                <a href={`/dex/pokemon/${variety.pokemon.url.split('/').slice(-2, -1)}`}>
                    {variety.is_default 
                        ? 'Base' 
                        : variety.pokemon.name.replace(species.name, '').replace('-', '').charAt(0).toUpperCase() + variety.pokemon.name.replace(species.name, '').replace('-', '').slice(1)}
                </a>
            </div>
        ));
    };

    const renderChain = (chainLink: EvolutionChainLink): React.ReactNode[] => {
        // Base case: if the Pokémon has no further evolutions, just return itself.
        if (chainLink.evolves_to.length === 0) {
            return [
                <div key={chainLink.species.name} className="chain-item">
                    <img
                        src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${chainLink.species.url.split('/').slice(-2, -1)}.png`}
                        alt={chainLink.species.name}
                        style={{ width: '35px' }}
                    />
                    <a href={`/dex/pokemon/${chainLink.species.url.split('/').slice(-2, -1)}`} style={{ marginRight: '5px' }}>
                        {chainLink.species.name.charAt(0).toUpperCase() + chainLink.species.name.slice(1)}
                    </a>
                </div>,
            ];
        }

        // Recursive case: generate the chain for the current Pokémon and its evolutions.
        const currentLink = (
            <div key={chainLink.species.name} className="chain-item">
                <img
                    src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${chainLink.species.url.split('/').slice(-2, -1)}.png`}
                    alt={chainLink.species.name}
                    style={{ width: '35px' }}
                />
                <a href={`/dex/pokemon/${chainLink.species.url.split('/').slice(-2, -1)}`} style={{ marginRight: '5px' }}>
                    {chainLink.species.name.charAt(0).toUpperCase() + chainLink.species.name.slice(1)}
                </a>
            </div>
        );

        const nextEvolutions = chainLink.evolves_to.map((evo) => renderChain(evo));

        // Flatten the array to keep everything on the same level (not nested)
        const flattenedEvolutions = nextEvolutions.flat();

        // If there's only one evolution, add an arrow and return the sequence
        if (flattenedEvolutions.length === 1) {
            return [currentLink, ' → ', ...flattenedEvolutions];
        }

        // If there are multiple evolutions, return them as a branch (comma-separated)
        return [currentLink, ' → ', <div style={{ display: 'inline-block' }}>{flattenedEvolutions.map((evo, idx) => (
            <span key={idx}>
                {evo}
                {idx < flattenedEvolutions.length - 1 ? ', ' : ''}
            </span>
        ))}</div>];
    };


    const hasMultipleVarieties = species.varieties.length > 1;
    const hasMultipleEvolutions = species.evolution_chain.evolves_to.length > 0;

    return (
        <Row>
            <Col md={4}>
                <img
                    src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${form.id}.png`}
                    alt={form.name}
                    style={{ width: '100%' }}
                />
                {hasMultipleVarieties && (
                    <>
                        <h4>Other Forms</h4>
                        <div className="varieties-container">
                            {renderVarieties()}
                        </div>
                    </>
                )}
                {hasMultipleEvolutions && (
                    <>
                        <h4>Evolution Chain</h4>
                        <div className="chain-container">
                            {renderChain(species.evolution_chain)}
                        </div>
                    </>
                )}
            </Col>
            <Col md={8}>
                <h3>Abilities</h3>
                <ul>
                    {form.abilities.map((ability, idx) => (
                        <li key={idx}>
                            {ability.name} {ability.is_hidden && '(Hidden)'}
                        </li>
                    ))}
                </ul>
                <h3>Stats</h3>
                <ul>
                    {form.stats.map((stat, idx) => (
                        <li key={idx}>
                            {stat.name.toUpperCase()}: {stat.base_stat}
                        </li>
                    ))}
                </ul>
                <h3>Types</h3>
                <ul>
                    {form.types.map((type, idx) => (
                        <li key={idx}>{type.name.toUpperCase()}</li>
                    ))}
                </ul>
                <p><strong>Height:</strong> {form.height} m</p>
                <p><strong>Weight:</strong> {form.weight} kg</p>
            </Col>
        </Row>
    );
};

export default PokemonBasicInfo;
