import React from 'react';
import { Row, Col } from 'react-bootstrap';

interface Ability {
    name: string;
    is_hidden: boolean;
}

interface Stat {
    name: string;
    base_stat: number;
}

interface Type {
    name: string;
}

interface PokemonForm {
    id: number;
    name: string;
    abilities: Ability[];
    height: number;
    weight: number;
    stats: Stat[];
    types: Type[];
}

interface PokemonBasicInfoProps {
    form: PokemonForm;
}

const PokemonBasicInfo: React.FC<PokemonBasicInfoProps> = ({ form }) => {
    return (
        <Row>
            <Col md={4}>
                <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${form.id}.png`} alt={form.name} style={{ width: '100%' }} />
            </Col>
            <Col md={8}>
                <h3>Abilities</h3>
                <ul>
                    {form.abilities.map((ability, idx) => (
                        <li key={idx}>
                            {ability.name} {ability.is_hidden && '(Hidden)'}
                        </li>
                    ))}
                </ul>
                <h3>Stats</h3>
                <ul>
                    {form.stats.map((stat, idx) => (
                        <li key={idx}>
                            {stat.name.toUpperCase()}: {stat.base_stat}
                        </li>
                    ))}
                </ul>
                <h3>Types</h3>
                <ul>
                    {form.types.map((type, idx) => (
                        <li key={idx}>{type.name.toUpperCase()}</li>
                    ))}
                </ul>
                <p><strong>Height:</strong> {form.height} m</p>
                <p><strong>Weight:</strong> {form.weight} kg</p>
            </Col>
        </Row>
    );
};

export default PokemonBasicInfo;
