import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

interface City {
    name: string;
    population: number;
}

interface Region {
    name: string;
    population: number;
    cities: City[];
}

const RegionDetail: React.FC = () => {
    const { id } = useParams<{ id: string }>();
    const [region, setRegion] = useState<Region | null>(null);

    useEffect(() => {
        const fetchRegion = async () => {
            try {
                const response = await axios.get(`http://localhost:5000/regions/${id}`);
                setRegion(response.data.data);
            } catch (error) {
                console.error('Error fetching region:', error);
            }
        };

        fetchRegion();
    }, [id]);

    if (!region) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>{region.name}</h1>
            <p>Population: {region.population.toLocaleString()}</p>
            <h2>Cities</h2>
            <ul>
                {region.cities.map((city, index) => (
                    <li key={index}>
                        {city.name} - Population: {city.population ? city.population.toLocaleString() : 'N/A'}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default RegionDetail;
