    //             const nationalDexNumber = speciesData.pokedex_numbers.find((entry: any) => entry.pokedex.name === 'national').entry_number;
    //             const generation = speciesData.generation.name.replace('generation-', 'Generation ').toUpperCase();
    //             const genus = speciesData.genera.find((entry: any) => entry.language.name === 'en').genus;
    //             const varieties = speciesData.varieties;
