import React, { useState, useMemo } from 'react';
import { Table } from 'react-bootstrap';

interface SortableTableProps<T> {
    columns: Array<{ key: keyof T, label: string }>;
    data: T[];
}

const SortableTable = <T extends {}>({ columns, data }: SortableTableProps<T>) => {
    const [sortConfig, setSortConfig] = useState<{ key: keyof T, direction: string } | null>(null);

    const sortedData = useMemo(() => {
        let sortableData = [...data];
        if (sortConfig !== null) {
            sortableData.sort((a, b) => {
                if (a[sortConfig.key] < b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? -1 : 1;
                }
                if (a[sortConfig.key] > b[sortConfig.key]) {
                    return sortConfig.direction === 'ascending' ? 1 : -1;
                }
                return 0;
            });
        }
        return sortableData;
    }, [data, sortConfig]);

    const requestSort = (key: keyof T) => {
        let direction = 'ascending';
        if (sortConfig && sortConfig.key === key && sortConfig.direction === 'ascending') {
            direction = 'descending';
        }
        setSortConfig({ key, direction });
    };

    const getClassNamesFor = (key: keyof T) => {
        if (!sortConfig) {
            return;
        }
        return sortConfig.key === key ? sortConfig.direction : undefined;
    };

    return (
        <Table hover>
            <thead>
                <tr>
                    {columns.map((column) => (
                        <th
                            key={column.key as string}
                            onClick={() => requestSort(column.key)}
                            className={getClassNamesFor(column.key)}
                        >
                            {column.label} {sortConfig?.key === column.key ? (sortConfig.direction === 'ascending' ? '▲' : '▼') : ''}
                        </th>
                    ))}
                </tr>
            </thead>
            <tbody>
                {sortedData.map((item, index) => (
                    <tr key={index}>
                        {columns.map((column) => (
                            <td key={column.key as string}>
                                {item[column.key] as React.ReactNode}
                            </td>
                        ))}
                    </tr>
                ))}
            </tbody>
        </Table>
    );
};

export default SortableTable;
